declare module '*.svg';
declare module '*.png';
declare module '*.jpg';
declare module '*.jpeg';
declare module '*.gif';
declare module '*.bmp';
declare module '*.tiff';
declare module '*.less';
declare module '*.scss'; // declare module 语句用于声明一个模块，并指定模块的类型定义。*.scss 表示该模块包含所有以 .scss 后缀结尾的文件。这个语句的作用是为 SCSS 样式文件创建一个空的类型定义，告诉 TypeScript 编译器这些文件的类型是可以被识别和使用的。
declare module 'react-css-modules';
